import base64, struct
# BMP图的头格式 
s = b'\x42\x4d\x38\x8c\x0a\x00\x00\x00\x00\x00\x36\x00\x00\x00\x28\x00\x00\x00\x80\x02\x00\x00\x68\x01\x00\x00\x01\x00\x18\x00'

bs = struct.unpack('<ccIIIIIIHH', s)
print(bs)


bmp_data = base64.b64decode('')

def bmp_info(data):
    f = struct.unpack('<ccIIIIIIHH', data[:30])
    print(f)
    return {
        'width': f[-4],
        'height': f[-3],
        'color': f[-1]
    }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')